import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Login from "./pages/Auth/AuthForm";
import Signup from "./pages/Auth/Signup";
import AdminDashboard from "./pages/Admin/AdminDashboard";
import AlumniDashboard from "./pages/Alumni/AlumniDashboard";
import StudentDashboard from "./pages/Student/StudentDashboard";
import RequireAuth from "./components/RequireAuth";
import { AuthProvider } from "./context/AuthProvider";

function App() {
  return (
    <AuthProvider>
      <Router>
        <Routes>
          <Route path="/signup" element={<Signup />} />
          <Route path="/login" element={<Login />} />
          <Route path="/unauthorized" element={<h1>Unauthorized Access</h1>} />

          {/* Role-Based Protected Routes */}
          <Route path="/admin" element={<RequireAuth allowedRoles={["admin"]}><AdminDashboard /></RequireAuth>} />
          <Route path="/alumni" element={<RequireAuth allowedRoles={["alumni"]}><AlumniDashboard /></RequireAuth>} />
          <Route path="/student" element={<RequireAuth allowedRoles={["student"]}><StudentDashboard /></RequireAuth>} />
        </Routes>
      </Router>
    </AuthProvider>
  );
}

export default App;
